import React, { useContext } from "react";
import { Conversation } from "../../ChatApp";
import { UserContext } from "../../contexts/UserProvider";
import SideBarConversationItem from "./sideBarItem/SideBarConversationItem";
import "./sideBarConversationsList.css";
import SideBarSearchInput from "./sideBarSearchInput/SideBarSearchInput";

const ConversationSideBar = ({
  conversations,
  setCurrentConversation,
}: {
  conversations: Conversation[];
  setCurrentConversation: React.Dispatch<
    React.SetStateAction<Conversation | null>
  >;
}) => {
  const userContext = useContext(UserContext);

  return (
    <div className="side-bar-conversations-list-container">
      <SideBarSearchInput />
      <div className="side-bar-conversation-list-wrapper">
        {conversations.map((conversation) => (
          <SideBarConversationItem
            key={conversation._id}
            conversation={conversation}
            setCurrentConversation={setCurrentConversation}
          />
        ))}
      </div>
    </div>
  );
};

export default ConversationSideBar;
