import { DateTime } from "luxon";
import { Message } from "../../../../../ChatApp";

export const hasAboveNeighborOfSameTime = ({
  messages,
  i,
}: {
  messages: Message[];
  i: number;
}) => {
  const messageAbove = messages[i + 1];

  const date = DateTime.fromISO(messages[i].createdAt).toLocaleString({
    month: "2-digit",
    day: "2-digit",
    hour: "2-digit",
    minute: "2-digit",
    hourCycle: "h23",
    year: "2-digit",
  });
  console.log(date);

  return true;
};
