import axios from "axios";
import React, { useEffect, useState } from "react";
import io, { Socket } from "socket.io-client";
import ConversationSideBar from "./components/sideBarConversationsList/SideBarConversationsList";
import { UserContext } from "./contexts/UserProvider";
import "./app.css";
import MessagesList from "./components/chat/messagesList/MessagesList";
import ChatInput from "./components/chat/chatInput/ChatInput";
// import moment from "moment";

export type User = {
  _id: string;
  username: string;
  email: string;
  createdAt: Date;
  updatedAt: Date;
  __v: 0;
};

export type Conversation = {
  _id: string;
  createdAt: Date;
  updatedAt: Date;
  __v: number;
  membersInfos: User[];
};

export type Message = {
  _id: string;
  content: string;
  userInfos: User;
  conversationID: string;
  createdAt: Date;
  updatedAt: Date;
  __v: number;
};

const App = () => {
  // useEffect(() => {
  //   localStorage.setItem(
  //     "user",
  //     JSON.stringify({
  //       _id: "6214bf8be824cba2c81db0dd",
  //       username: "second",
  //       email: "secondEmail",
  //       createdAt: "2022-02-22T10:48:43.521+00:00",
  //       updatedAt: "2022-02-22T10:48:43.521+00:00",
  //       __v: 0,
  //     })
  //   );
  // }, []);

  const [user, setUser] = useState<User | null>(null);
  const [messages, setMessages] = useState<Message[]>([]);
  const [message, setMessage] = useState<string>("");
  const [conversations, setConversations] = useState<Conversation[]>([]);
  const [selectedConversation, setSelectedConversation] =
    useState<Conversation | null>(null);

  useEffect(() => {
    const userJson = localStorage.getItem("user");
    userJson && setUser(JSON.parse(userJson));
  }, []);

  //WEB SOCKET
  const [socket, setSocket] = useState<Socket | null>(null);
  useEffect(() => {
    setSocket(io("http://localhost:8080"));
  }, []);

  useEffect(() => {
    socket?.emit("newUser", user?._id);
  }, [socket, user]);

  useEffect(() => {
    socket?.on("newMessage", (message) => {
      setMessages((prev) => [...prev, message]);
    });
  }, [socket]);
  //WEB SOCKET

  useEffect(() => {
    const getConversations = async () => {
      const res = await axios.get(
        `http://localhost:5050/api/conversations/${user?._id}`
      );
      setConversations(res.data);
    };
    getConversations();
  }, [user]);

  useEffect(() => {
    const getMessages = async () => {
      const res = await axios.get(
        `http://localhost:5050/api/messages/${selectedConversation?._id}`
      );
      setMessages(res.data);
    };
    getMessages();
  }, [selectedConversation]);

  const sendMessage = async () => {
    if (message === "" || !selectedConversation || !user) return;
    const res = await axios.post(
      `http://localhost:5050/api/messages/${selectedConversation?._id}`,
      {
        content: message,
        userID: user._id,
        conversationID: selectedConversation._id,
      }
    );
    const membersIds = selectedConversation.membersInfos.map(
      (member) => member._id
    );
    const receiversIds = membersIds.filter((id) => id !== user._id);
    const { userID, ...others } = res.data;
    const messageCopy = {
      ...others,
      userInfos: user,
    };
    socket?.emit("newMessage", {
      message: messageCopy,
      receiversIds: receiversIds,
    });
    setMessages([...messages, messageCopy]);
    setMessage("");
  };

  return (
    <UserContext.Provider value={{ user, setUser }}>
      <div className="container">
        <ConversationSideBar
          conversations={conversations}
          setSelectedConversation={setSelectedConversation}
        />
        <div className="chat-box">
          <MessagesList messages={messages} />

          <ChatInput />
        </div>
      </div>
    </UserContext.Provider>
  );
};

export default App;
