import moment from "moment";
import React, { useContext, useEffect, useRef } from "react";
import { Message } from "../../../../ChatApp";
import { UserContext } from "../../../../contexts/UserProvider";
import { hasCloseNeighbor } from "../isMessageClose";
import "./chatMessage.css";
import { format } from "timeago.js";
import { DateTime } from "luxon";

const ChatMessage = ({
  message,
  messages,
  i,
}: {
  message: Message;
  messages: Message[];
  i: number;
}) => {
  const { user } = useContext(UserContext);
  const messageRef = useRef<HTMLDivElement>(null);
  const isOwnMessage = user?._id === message.userInfos._id;
  const hasNeighborBelow = hasCloseNeighbor({
    order: "next",
    messages,
    i,
  });
  const hasNeighborAbove = hasCloseNeighbor({
    order: "prev",
    messages,
    i,
  });

  const messageTime = () => {
    const today = DateTime.fromISO(new Date().toISOString()).toLocaleString();
    const messageDate = DateTime.fromISO(message.createdAt).toLocaleString();
    if (today !== messageDate) {
      return DateTime.fromRFC2822(message.createdAt).toLocaleString({
        month: "2-digit",
        day: "2-digit",
        hour: "2-digit",
        minute: "2-digit",
        hourCycle: "h23",
      });
    } else {
      return DateTime.fromISO(message.createdAt).toLocaleString({
        hour: "2-digit",
        minute: "2-digit",
        hourCycle: "h23",
      });
    }
  };

  const className = () => {
    if (isOwnMessage && hasNeighborBelow && hasNeighborAbove)
      return "conversation-message own-up-down";
    if (isOwnMessage && hasNeighborBelow && !hasNeighborAbove)
      return "conversation-message own-down";
    if (isOwnMessage && !hasNeighborBelow && hasNeighborAbove)
      return "conversation-message own-up";
    if (isOwnMessage && !hasNeighborBelow && !hasNeighborAbove)
      return "conversation-message own";

    if (!isOwnMessage && hasNeighborBelow && hasNeighborAbove)
      return "conversation-message up-down";
    if (!isOwnMessage && hasNeighborBelow && !hasNeighborAbove)
      return "conversation-message down";
    if (!isOwnMessage && !hasNeighborBelow && hasNeighborAbove)
      return "conversation-message up";

    return "conversation-message";
  };

  useEffect(() => {
    if (message._id === messages[0]._id)
      messageRef.current?.scrollIntoView({
        behavior: "smooth",
      });
  }, [messageRef]);

  return (
    <div
      ref={messageRef}
      key={message._id}
      className={
        isOwnMessage
          ? "conversation-message-wrapper own"
          : "conversation-message-wrapper"
      }
    >
      <p className={className()}>{message.content}</p>
      {!hasNeighborBelow && (
        <p className="conversation-message-date">{messageTime()}</p>
      )}
    </div>
  );
};

export default ChatMessage;
