    password: "$2b$10$GSh2pLpCmxsg/cAWhScVA.dbKMSBLxMK.viZqiqBZNunJebqkx50m",
    createdAt: "2022-02-22T10:45:10.155+00:00",
    updatedAt: "2022-02-22T10:45:10.155+00:00",
    __v: 0,
  };

  const [messages, setMessages] = useState<
    {
      _id: string;
      content: string;
      userID: string;
      conversationID: string;
      createdAt: Date;
      updatedAt: Date;
      __v: number;
    }[]
  >([]);

  const [conversations, setConversations] = useState<
    {
      _id: string;
      createdAt: Date;
      updatedAt: Date;
      __v: number;
      membersInfos: {
        _id: string;
        username: string;
        email: string;
        createdAt: Date;
        updatedAt: Date;
        __v: number;
      }[];
    }[]
  >([]);

  useEffect(() => {
    const getConversations = async () => {
      const res = await axios.get(
        `http://localhost:5050/api/conversations/${user._id}`
      );
      setConversations(res.data);
    };
    getConversations();
  }, []);

  useEffect(() => {
    const getMessages = async () => {
      const res = await axios.get(
        "http://localhost:5050/api/messages/6214db1e0458d27ef9fcb1ff"
      );
      setMessages(res.data);
    };
    getMessages();
  }, []);

  return (
    <div style={{ display: "flex", marginRight: "1000px" }}>
      <div>
        {conversations.map((conversation) => {
          return (
            <div key={conversation._id}>
              {conversation.membersInfos.map((member) => {
