import axios from "axios";
import React, { useEffect, useState } from "react";

import io from "socket.io-client";
// import moment from "moment";

const App = () => {
  // const submit = (e: any) => {
  //   e.preventDefault();
  //   socket.emit("send", message);
  //   setMessage("");
  // };

  const user = {
    _id: "6214beb6e824cba2c81db0da",
    username: "first",
    email: "firstEmail",
    password: "$2b$10$GSh2pLpCmxsg/cAWhScVA.dbKMSBLxMK.viZqiqBZNunJebqkx50m",
    createdAt: "2022-02-22T10:45:10.155+00:00",
    updatedAt: "2022-02-22T10:45:10.155+00:00",
    __v: 0,
  };

  const [messages, setMessages] = useState<
    {
      _id: string;
      content: string;
      userID: string;
      conversationID: string;
      createdAt: Date;
      updatedAt: Date;
      __v: number;
    }[]
  >([]);

  const [conversations, setConversations] = useState<
    {
      _id: string;
      createdAt: Date;
      updatedAt: Date;
      __v: number;
      membersInfos: {
        _id: string;
        username: string;
        email: string;
        createdAt: Date;
        updatedAt: Date;
        __v: number;
      }[];
    }[]
  >([]);

  const [selectedConversation, setSelectedConversation] = useState<{
    _id: string;
    createdAt: Date;
    updatedAt: Date;
    __v: number;
    membersInfos: {
      _id: string;
      username: string;
      email: string;
      createdAt: Date;
      updatedAt: Date;
      __v: number;
    }[];
  }>();

  useEffect(() => {
    const getConversations = async () => {
      const res = await axios.get(
        `http://localhost:5050/api/conversations/${user._id}`
      );
      setConversations(res.data);
    };
    getConversations();
  }, []);

  useEffect(() => {
    const getMessages = async () => {
      const res = await axios.get(
        `http://localhost:5050/api/messages/${selectedConversation?._id}`
      );
      setMessages(res.data);
    };
    getMessages();
  }, [selectedConversation]);

  const [message, setMessage] = useState<string>("");

  const sendMessage = async () => {
    if (message === "" || !selectedConversation) return;
    const res = await axios.post(
      `http://localhost:5050/api/messages/${selectedConversation?._id}`,
      {
        content: message,
        userID: user._id,
        conversationID: selectedConversation._id,
      }
    );
    setMessages([
      ...messages,
      {
        _id: "1123456",
        content: message,
        userID: user._id,
        conversationID: selectedConversation._id,
        createdAt: new Date(),
        updatedAt: new Date(),
        __v: 0,
      },
    ]);
    setMessage("");
    socket.emit("message", res.data);
  };

  const socket = io("http://localhost:8080", {
    transports: ["websocket", "polling"],
  });
  const [users, setUsers] = useState<any>([]);

  useEffect(() => {
    socket.emit("connexion", user);
  }, []);

  useEffect(() => {
    socket.on("connect", () => {
      socket.emit("username", user.username);
    });

    socket.on("connexion", (users: any) => {
      setUsers(users);
    });

    socket.on("message", (message: any) => {
      setMessages([...messages, message]);
    });

    // socket.on("disconnected", (id) => {
    //   setUsers((users: any) => {
    //     return users.filter((user: any) => user.id !== id);
    //   });
    // });
  }, []);

  return (
    <div style={{ display: "flex" }}>
      <div style={{ marginRight: "100px" }}>
        {conversations.map((conversation) => {
          return (
            <div key={conversation._id}>
              {conversation.membersInfos.map((member) => {
                if (member._id !== user._id)
                  return (
                    <div
                      onClick={() => setSelectedConversation(conversation)}
                      key={member._id}
                    >
                      {member.username}
                    </div>
                  );
              })}
            </div>
          );
        })}
      </div>
      <div>
        {messages.map((message) => {
          return <div key={message._id}>{message.content}</div>;
        })}
        <input
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="your message"
        ></input>
        <button onClick={sendMessage}>SEND</button>
      </div>
    </div>
  );
};

export default App;
