import { Message } from "../../../../types";

export const scrollIntoView = ({
  messageRef,
  messages,
  i,
}: {
  messageRef: React.RefObject<HTMLDivElement>;
  messages: Message[];
  i: number;
}) => {
  console.log(messages[i]._id, messages[messages.length]._id);
  // if (messages[i]._id === messages[messages.length - 1]._id)
  messageRef.current?.scrollIntoView({
    behavior: "smooth",
  });
};
