import moment from "moment";
import React, { useContext } from "react";
import { Message } from "../../../ChatApp";
import { UserContext } from "../../../contexts/UserProvider";
import "./messageConversationList.css";

const MessagesList = ({ messages }: { messages: Message[] }) => {
  const { user, setUser } = useContext(UserContext);
  // console.log(moment(messages[0]?.createdAt).format());

  return (
    <div className="conversation-message-list-container">
      {messages.map((message, i) => {
        const isOwnMessage = user?._id === message.userInfos._id;

        if (messages[i + 1]) {
          if (
            moment(message.createdAt).format().substring(0, 4) <
            moment(messages[i + 1].createdAt)
              .format()
              .substring(0, 4)
          )
            return;
          if (
            moment(message.createdAt).format().substring(5, 7) <
            moment(messages[i + 1].createdAt)
              .format()
              .substring(5, 7)
          )
            return;
          if (
            moment(message.createdAt).format().substring(8, 10) <
            moment(messages[i + 1].createdAt)
              .format()
              .substring(8, 10)
          )
            return;
          if (
            moment(message.createdAt).format().substring(12, 14) <
            moment(messages[i + 1].createdAt)
              .format()
              .substring(12, 14)
          ) {
            console.log("first");
            return;
          }
          if (
            moment(message.createdAt).format().substring(15, 17) <
            moment(messages[i + 1].createdAt)
              .format()
              .substring(15, 17)
          )
            return;
          console.log(moment(message.createdAt).format().substring(11, 13));
          // console.log(true);
        }

        return (
          <div
            key={message._id}
            className={
              isOwnMessage
                ? "conversation-message-wrapper own"
                : "conversation-message-wrapper"
            }
          >
            <p
              className={
                isOwnMessage
                  ? "conversation-message own"
                  : "conversation-message"
              }
            >
              {message.content}
            </p>
            <p className="conversation-message-date">
              {moment(message.createdAt).format("H:mm")}
            </p>
          </div>
        );
      })}
    </div>
  );
};

export default MessagesList;
