import React, { useContext, useEffect, useRef } from "react";
import { Message } from "../../../../ChatApp";
import { UserContext } from "../../../../contexts/UserProvider";
import { hasCloseNeighbor } from "../isMessageClose";
import "./chatMessage.css";
import { DateTime } from "luxon";
import { scrollIntoView } from "./scrollIntoView";
import ProfilePicture from "../../../profilePicture/ProfilePicture";

const ChatMessage = ({
  message,
  messages,
  i,
}: {
  message: Message;
  messages: Message[];
  i: number;
}) => {
  const { user } = useContext(UserContext);
  const messageRef = useRef<HTMLDivElement>(null);
  const isOwnMessage = user?._id === message.userInfos._id;
  const hasNeighborBelow = hasCloseNeighbor({
    order: "next",
    messages,
    i,
  });
  const hasNeighborAbove = hasCloseNeighbor({
    order: "prev",
    messages,
    i,
  });

  const messageTime = () => {
    const today = DateTime.fromISO(new Date().toISOString()).toLocaleString();
    const messageDate = DateTime.fromISO(message.createdAt).toLocaleString();
    if (today !== messageDate) {
      return DateTime.fromISO(message.createdAt).toLocaleString({
        month: "2-digit",
        day: "2-digit",
        hour: "2-digit",
        minute: "2-digit",
        hourCycle: "h23",
      });
    } else {
      return DateTime.fromISO(message.createdAt).toLocaleString({
        hour: "2-digit",
        minute: "2-digit",
        hourCycle: "h23",
      });
    }
  };

  const className = () => {
    if (isOwnMessage && hasNeighborBelow && hasNeighborAbove)
      return "chat-message own-up-down";
    if (isOwnMessage && hasNeighborBelow && !hasNeighborAbove)
      return "chat-message own-down";
    if (isOwnMessage && !hasNeighborBelow && hasNeighborAbove)
      return "chat-message own-up";
    if (isOwnMessage && !hasNeighborBelow && !hasNeighborAbove)
      return "chat-message own";

    if (!isOwnMessage && hasNeighborBelow && hasNeighborAbove)
      return "chat-message up-down";
    if (!isOwnMessage && hasNeighborBelow && !hasNeighborAbove)
      return "chat-message down";
    if (!isOwnMessage && !hasNeighborBelow && hasNeighborAbove)
      return "chat-message up";

    return "chat-message";
  };

  useEffect(() => {
    scrollIntoView(messageRef);
  }, [messageRef]);

  return (
    <div
      ref={messageRef}
      key={message._id}
      className={
        isOwnMessage ? "chat-message-wrapper own" : "chat-message-wrapper"
      }
    >
      {!hasNeighborAbove && (
        <div className="chat-message-date-wrapper">
          <p className="chat-message-date">{messageTime()}</p>
          <div className="chat-message-date-divider" />
        </div>
      )}
      <div className="chat-message-text-PP">
        {!hasNeighborBelow && !isOwnMessage && (
          <ProfilePicture members={[message.userInfos]} size="small" />
        )}
        <p className={className()}>{message.content}</p>
      </div>
    </div>
  );
};

export default ChatMessage;
