import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { Conversation } from "../../ChatApp";
import { UserContext } from "../../contexts/UserProvider";
import "./sideBarConversationsList.css";

const ConversationSideBar = ({
  conversations,
  setSelectedConversation,
}: {
  conversations: Conversation[];
  setSelectedConversation: React.Dispatch<
    React.SetStateAction<Conversation | null>
  >;
}) => {
  const user = useContext(UserContext);

  return (
    <div className="side-bar-conversations-list-container">
      {conversations.map((conversation) => {
        return (
          <Link to={conversation._id} key={conversation._id}>
            {conversation.membersInfos.map((member) => {
              return (
                member._id !== user?.user?._id && (
                  <div
                    onClick={() => setSelectedConversation(conversation)}
                    key={member._id}
                  >
                    {member.username}
                  </div>
                )
              );
            })}
          </Link>
        );
      })}
    </div>
  );
};

export default ConversationSideBar;
