import axios from "axios";
import React, { useEffect, useState } from "react";
import io, { Socket } from "socket.io-client";
import ConversationSideBar from "./components/sideBarConversationsList/SideBarConversationsList";
import { UserContext } from "./contexts/UserProvider";
import "./chatApp.css";
import MessagesList from "./components/chat/messagesList/MessagesList";
import ChatInput from "./components/chat/chatInput/ChatInput";
import { useParams } from "react-router-dom";
import ChatHeader from "./components/chat/chatHeader/ChatHeader";

export type User = {
  _id: string;
  username: string;
  email: string;
  profilePicture: string;
  createdAt: string;
  updatedAt: string;
};
export type Message = {
  _id: string;
  content: string;
  userInfos: User;
  conversationID: string;
  createdAt: string;
  updatedAt: string;
};
export interface Conversation {
  _id: string;
  createdAt: string;
  updatedAt: string;
  membersInfos: User[];
  lastMessage: {
    _id: string;
    content: string;
    userID: string;
    createdAt: string;
    updatedAt: string;
  };
}

const App = () => {
  const [user, setUser] = useState<User | null>(null);
  const [messages, setMessages] = useState<Message[]>([]);
  const [conversations, setConversations] = useState<Conversation[]>([]);
  const [currentConversation, setCurrentConversation] =
    useState<Conversation | null>(null);
  const currentConversationIdParams = useParams().conversationId;

  useEffect(() => {
    const foundCurrentConversation = conversations.find(
      (conversation) => conversation._id === currentConversationIdParams
    );
    if (!foundCurrentConversation) return;
    setCurrentConversation(foundCurrentConversation);
  }, [currentConversationIdParams, conversations]);

  //GET USER
  useEffect(() => {
    const userJson = localStorage.getItem("user");
    userJson && setUser(JSON.parse(userJson));
  }, []);

  //WEB SOCKET
  const [socket, setSocket] = useState<Socket | null>(null);
  useEffect(() => {
    setSocket(io("http://localhost:8080"));
  }, []);
  useEffect(() => {
    socket?.emit("newUser", user?._id);
  }, [socket, user]);
  useEffect(() => {
    socket?.on("newMessage", (message) => {
      setMessages((prev) => [...prev, message]);
    });
  }, [socket]);

  const emitNewMessageSocket = (message: Message, receiversIds: string[]) => {
    socket?.emit("newMessage", {
      message,
      receiversIds,
    });
  };

  const updateConversationLastMessage = (message: Message) => {
    if (!currentConversation) return;
    console.log(currentConversation.lastMessage, message);
    // setCurrentConversation({ ...currentConversation, lastMessage: message });
  };

  const sortMessages = (data: Message[]) => {
    setMessages(
      data.sort((m1: Message, m2: Message) => {
        return (
          new Date(m2.createdAt).valueOf() - new Date(m1.createdAt).valueOf()
        );
      })
    );
  };

  //GET CONVERSATIONS
  useEffect(() => {
    const getConversations = async () => {
      const res = await axios.get(
        `http://localhost:5050/api/conversations/${user?._id}`
      );
      setConversations(res.data);
    };
    getConversations();
  }, [user]);

  //GET MESSAGES
  useEffect(() => {
    if (!currentConversationIdParams) return;
    const getMessages = async () => {
      const res = await axios.get(
        `http://localhost:5050/api/messages/${currentConversationIdParams}`
      );
      sortMessages(res.data);
    };
    getMessages();
  }, [currentConversationIdParams]);

  //SEND MESSAGE
  const sendMessage = async ({
    messageInput,
    setMessageInput,
  }: {
    messageInput: string;
    setMessageInput: React.Dispatch<React.SetStateAction<string>>;
  }) => {
    if (messageInput === "" || !currentConversation || !user) return;
    const res = await axios.post(
      `http://localhost:5050/api/messages/${currentConversationIdParams}`,
      {
        content: messageInput,
        userID: user._id,
        conversationID: currentConversationIdParams,
      }
    );
    const membersIds = currentConversation.membersInfos.map(
      (member) => member._id
    );
    const receiversIds = membersIds.filter((id) => id !== user._id);
    const { userID, ...others } = res.data;
    const messageCopy = {
      ...others,
      userInfos: user,
    };
    emitNewMessageSocket(messageCopy, receiversIds);
    updateConversationLastMessage(messageCopy);
    setMessages([messageCopy, ...messages]);
    setMessageInput("");
  };

  return (
    <UserContext.Provider value={{ user, setUser }}>
      <div className="container">
        <ConversationSideBar conversations={conversations} />
        <div className="chat-box">
          <ChatHeader currentConversation={currentConversation} />
          <MessagesList messages={messages} />
          <ChatInput sendMessage={sendMessage} />
        </div>
      </div>
    </UserContext.Provider>
  );
};

export default App;
