import React, { useEffect, useRef } from "react";
import { Message } from "../../../types";
import ChatMessage from "./chatMessage/ChatMessage";
import "./messagesList.css";

const MessagesList = ({ messages }: { messages: Message[] }) => {
  const chatMessagesContainerRef = useRef<HTMLDivElement>(null);
  useEffect(() => {
    if (!chatMessagesContainerRef.current) return;
    const handleScroll = () => {
      if (chatMessagesContainerRef.current)
        console.log(
          chatMessagesContainerRef.current.getBoundingClientRect().top
        );
    };

    chatMessagesContainerRef.current.addEventListener("scroll", handleScroll);
    return () =>
      chatMessagesContainerRef.current?.removeEventListener(
        "scroll",
        handleScroll
      );
  }, [chatMessagesContainerRef]);

  return (
    <div className="chat-messages-list-container">
      <div
        ref={chatMessagesContainerRef}
        className="chat-messages-list-wrapper"
      >
        {messages.map((message, i) => (
          <ChatMessage key={message._id} messages={messages} i={i} />
        ))}
      </div>
    </div>
  );
};

export default MessagesList;
