import React from "react";
import "chatDate.css";
import { hasCloseNeighbor } from "../../isMessageClose";
import { Message } from "../../../../../ChatApp";
import { DateTime } from "luxon";

const ChatDate = ({ messages, i }: { messages: Message[]; i: number }) => {
  const hasNeighborAbove = hasCloseNeighbor({
    order: "prev",
    messages,
    i,
  });

  const messageTime = () => {
    const today = DateTime.fromISO(new Date().toISOString()).toLocaleString();
    const messageDate = DateTime.fromISO(
      messages[i].createdAt
    ).toLocaleString();
    if (today !== messageDate) {
      return DateTime.fromISO(messages[i].createdAt).toLocaleString({
        month: "2-digit",
        day: "2-digit",
        hour: "2-digit",
        minute: "2-digit",
        hourCycle: "h23",
      });
    } else {
      return DateTime.fromISO(messages[i].createdAt).toLocaleString({
        hour: "2-digit",
        minute: "2-digit",
        hourCycle: "h23",
      });
    }
  };

  return !hasNeighborAbove ? (
    <div className="chat-message-date-wrapper">
      <p className="chat-message-date">{messageTime()}</p>
      <div className="chat-message-date-divider" />
    </div>
  ) : (
    <></>
  );
};

export default ChatDate;
