    const index = Math.floor((start + end) / 2)
    const item = items[index]
    const match = comparator(item, value)
    if (match === 0) {
      return index
    }

    if (match === -1) {
      if (end - start < 2) {
        return index - 1
      }
      end = index - 1
    } else {
      if (end === start) {
        return index
      }
      start = index + 1
    }
  }

  throw new Error(`Failed binary finding record in array - ${items.join(',')}, searched for ${value}`)
