import { relativeTimeRounding } from "moment";
import React, { useEffect, useRef } from "react";
import { Message } from "../../../types";
import ChatMessage from "./chatMessage/ChatMessage";
import "./messagesList.css";

const MessagesList = ({ messages }: { messages: Message[] }) => {
  const chatMessagesWrapperRef = useRef<HTMLDivElement>(null);
  useEffect(() => {
    if (!chatMessagesWrapperRef.current) return;
    const handleScroll = () => {
      if (!chatMessagesWrapperRef.current) return;
      const scrollableHeight =
        chatMessagesWrapperRef.current.scrollHeight -
        chatMessagesWrapperRef.current.getBoundingClientRect().height;

      const hasReachedTop = () => {
        if (!chatMessagesWrapperRef.current) return;
        if (scrollableHeight + chatMessagesWrapperRef.current.scrollTop > 0)
          return true;
        else return false;
      };
      console.log(hasReachedTop());
    };

    chatMessagesWrapperRef.current.addEventListener("scroll", handleScroll);
    return () =>
      chatMessagesWrapperRef.current?.removeEventListener(
        "scroll",
        handleScroll
      );
  }, [chatMessagesWrapperRef]);

  return (
    <div className="chat-messages-list-container">
      <div ref={chatMessagesWrapperRef} className="chat-messages-list-wrapper">
        {messages.map((message, i) => (
          <ChatMessage key={message._id} messages={messages} i={i} />
        ))}
      </div>
    </div>
  );
};

export default MessagesList;
