import React, { useContext } from "react";
import { Chats } from "../../ChatApp";
import { UserContext } from "../../contexts/UserProvider";
import SideBarChatItem from "./sideBarItem/SideBarChatItem";
import "./sideBarChatsList.css";
import SideBarHeader from "./sideBarHeader/SideBarHeader";

const ChatsSideBar = ({ chats }: { chats: Chats[] }) => {
  const { user } = useContext(UserContext);

  return (
    <div className="side-bar-conversations-list-container">
      <SideBarHeader />
      <div className="side-bar-conversations-list-wrapper">
        {chats.map((chat) => (
          <SideBarChatItem key={chat._id} conversation={chat} />
        ))}
      </div>
    </div>
  );
};

export default ChatsSideBar;
