import axios from "axios";
import React, { useEffect, useState } from "react";
import io, { Socket } from "socket.io-client";
// import moment from "moment";

type User = {
  _id: "";
  username: "";
  email: "";
  createdAt: Date;
  updatedAt: Date;
  __v: 0;
};

type Conversation = {
  _id: string;
  createdAt: Date;
  updatedAt: Date;
  __v: number;
  membersInfos: User[];
};

type Message = {
  _id: string;
  content: string;
  userInfos: User;
  conversationID: string;
  createdAt: Date;
  updatedAt: Date;
  __v: number;
};

const App = () => {
  // useEffect(() => {
  //   localStorage.setItem(
  //     "user",
  //     JSON.stringify({
  //       _id: "6214bf8be824cba2c81db0dd",
  //       username: "second",
  //       email: "secondEmail",
  //       createdAt: "2022-02-22T10:48:43.521+00:00",
  //       updatedAt: "2022-02-22T10:48:43.521+00:00",
  //       __v: 0,
  //     })
  //   );
  // }, []);

  const [user, setUser] = useState<User>();
  const [messages, setMessages] = useState<Message[]>([]);

  const [conversations, setConversations] = useState<
    {
      _id: string;
      createdAt: Date;
      updatedAt: Date;
      __v: number;
      membersInfos: User[];
    }[]
  >([]);

  useEffect(() => {
    const userJson = localStorage.getItem("user");
    userJson && setUser(JSON.parse(userJson));
  }, []);

  //WEB SOCKET
  const [socket, setSocket] = useState<Socket | null>(null);
  useEffect(() => {
    setSocket(io("http://localhost:8080"));
  }, []);

  useEffect(() => {
    socket?.emit("newUser", user?._id);
  }, [socket, user]);

  useEffect(() => {
    socket?.on("newMessage", (message) => {
      setMessages((prev) => [...prev, message]);
    });
  }, [socket]);

  const [selectedConversation, setSelectedConversation] =
    useState<Conversation>();

  useEffect(() => {
    const getConversations = async () => {
      const res = await axios.get(
        `http://localhost:5050/api/conversations/${user?._id}`
      );
      setConversations(res.data);
    };
    getConversations();
  }, [user]);

  useEffect(() => {
    const getMessages = async () => {
      const res = await axios.get(
        `http://localhost:5050/api/messages/${selectedConversation?._id}`
      );
      setMessages(res.data);
    };
    getMessages();
  }, [selectedConversation]);

  const [message, setMessage] = useState<string>("");
  const sendMessage = async () => {
    if (message === "" || !selectedConversation || !user) return;
    const res = await axios.post(
      `http://localhost:5050/api/messages/${selectedConversation?._id}`,
      {
        content: message,
        userID: user._id,
        conversationID: selectedConversation._id,
      }
    );
    const membersIds = selectedConversation.membersInfos.map(
      (member) => member._id
    );
    const receiversIds = membersIds.filter((id) => id !== user._id);
    const { userID, ...others } = res.data;
    const messageCopy = {
      ...others,
      userInfos: user,
    };
    socket?.emit("newMessage", {
      message: messageCopy,
      receiversIds: receiversIds,
    });
    setMessages([...messages, messageCopy]);
    setMessage("");
  };

  return (
    <div style={{ display: "flex" }}>
      <div style={{ marginRight: "100px" }}>
        {conversations.map((conversation) => {
          return (
            <div key={conversation._id}>
              {conversation.membersInfos.map((member) => {
                return (
                  member._id !== user?._id && (
                    <div
                      onClick={() => setSelectedConversation(conversation)}
                      key={member._id}
                    >
                      {member.username}
                    </div>
                  )
                );
              })}
            </div>
          );
        })}
      </div>
      <div>
        {messages.map((message) => {
          return (
            <div
              key={message._id}
              style={
                message.userInfos._id === user?._id
                  ? { backgroundColor: "green" }
                  : { backgroundColor: "blue" }
              }
            >
              {message.content}
            </div>
          );
        })}
        <input
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="your message"
        ></input>
        <button onClick={sendMessage}>SEND</button>
      </div>
    </div>
  );
};

export default App;
