import moment from "moment";
import { Message } from "../../../ChatApp";

export const isMessageClose = ({
  order,
  messages,
  i,
}: {
  order: string;
  messages: Message[];
  i: number;
}) => {
  const neighborMessageTime =
    order === "next" ? messages[i + 1]?.createdAt : messages[i - 1]?.createdAt;
  const currentMessage = messages[i].createdAt;

  const time = (messageCreatedAt: Date, dateType: string) => {
    const table = {
      year: moment(messageCreatedAt).format().substring(0, 4),
      month: moment(messageCreatedAt).format().substring(5, 7),
      day: moment(messageCreatedAt).format().substring(8, 10),
      hour: moment(messageCreatedAt).format().substring(11, 13),
      minute: moment(messageCreatedAt).format().substring(14, 16),
    };
    const isValidDateType =
      dateType === "year" ||
      dateType === "month" ||
      dateType === "day" ||
      dateType === "hour" ||
      dateType === "minute";

    return table[isValidDateType ? dateType : "year"];
  };

  if (!messages[i + 1] || !neighborMessageTime) return false;

  if (time(currentMessage, "year") < time(neighborMessageTime, "year"))
    return false;
  if (time(currentMessage, "month") < time(neighborMessageTime, "month"))
    return false;

  if (time(currentMessage, "day") < time(neighborMessageTime, "day"))
    return false;

  if (time(currentMessage, "hour") < time(neighborMessageTime, "hour"))
    return false;

  if (time(currentMessage, "minute") < time(neighborMessageTime, "minute"))
    return false;
  console.log(time(currentMessage, "minute"));
  return true;
};
