import { Message } from "../../../../types";

export const scrollIntoView = (
  messageRef: React.RefObject<HTMLDivElement>,
  messages: Message[]
) => {
  if (messages)
    messageRef.current?.scrollIntoView({
      behavior: "smooth",
    });
};
