import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { Conversation } from "../../../ChatApp";
import { UserContext } from "../../../contexts/UserProvider";
import "./sideBarConversationItem.css";

const SideBarConversationItem = ({
  conversation,
  setCurrentConversation,
}: {
  conversation: Conversation;
  setCurrentConversation: React.Dispatch<
    React.SetStateAction<Conversation | null>
  >;
}) => {
  const userContext = useContext(UserContext);

  return (
    <Link
      className="Link"
      to={"/" + conversation._id}
      onClick={() => setCurrentConversation(conversation)}
      key={conversation._id}
    >
      <div className="side-bar-conversation-item">
        {conversation.membersInfos.map((member) => {
          return (
            <>
              {member.profilePicture ? (
                <img
                  className="side-bar-conversation-item-PP"
                  src={member.profilePicture}
                ></img>
              ) : (
                <div className="side-bar-conversation-item-initials">{"S"}</div>
              )}
            </>
          );
        })}
        {conversation.membersInfos.map((member) => {
          return <>{<div key={member._id}>{member.username}</div>}</>;
        })}
      </div>
    </Link>
  );
};

export default SideBarConversationItem;
