import axios from "axios";
import React, { useEffect, useState } from "react";
import io from "socket.io-client";
// import moment from "moment";

type User = {
  _id: "";
  username: "";
  email: "";
  createdAt: Date;
  updatedAt: Date;
  __v: 0;
};

const App = () => {
  const [user, setUser] = useState<User>();
  useEffect(() => {
    const userJson = localStorage.getItem("user");
    userJson && setUser(JSON.parse(userJson));
  }, []);
  // useEffect(() => {
  //   localStorage.setItem(
  //     "user",
  //     JSON.stringify({
  //       _id: "6214bf8be824cba2c81db0dd",
  //       username: "first",
  //       email: "firstEmail",
  //       password:
  //         "$2b$10$GSh2pLpCmxsg/cAWhScVA.dbKMSBLxMK.viZqiqBZNunJebqkx50m",
  //       createdAt: "2022-02-22T10:45:10.155+00:00",
  //       updatedAt: "2022-02-22T10:45:10.155+00:00",
  //       __v: 0,
  //     })
  //   );
  // }, []);

  const [messages, setMessages] = useState<
    {
      _id: string;
      content: string;
      userInfos: User;
      conversationID: string;
      createdAt: Date;
      updatedAt: Date;
      __v: number;
    }[]
  >([]);

  const [conversations, setConversations] = useState<
    {
      _id: string;
      createdAt: Date;
      updatedAt: Date;
      __v: number;
      membersInfos: User[];
    }[]
  >([]);

  const [selectedConversation, setSelectedConversation] = useState<{
    _id: string;
    createdAt: Date;
    updatedAt: Date;
    __v: number;
    membersInfos: User[];
  }>();

  useEffect(() => {
    const getConversations = async () => {
      const res = await axios.get(
        `http://localhost:5050/api/conversations/${user?._id}`
      );
      setConversations(res.data);
    };
    getConversations();
  }, [user]);

  useEffect(() => {
    const getMessages = async () => {
      const res = await axios.get(
        `http://localhost:5050/api/messages/${selectedConversation?._id}`
      );
      setMessages(res.data);
      console.log(res.data);
    };
    getMessages();
  }, [selectedConversation]);

  const [message, setMessage] = useState<string>("");

  const sendMessage = async () => {
    if (message === "" || !selectedConversation || !user) return;
    const res = await axios.post(
      `http://localhost:5050/api/messages/${selectedConversation?._id}`,
      {
        content: message,
        userID: user._id,
        conversationID: selectedConversation._id,
      }
    );
    setMessages([
      ...messages,
      {
        _id: "1123456",
        content: message,
        userInfos: user,
        conversationID: selectedConversation._id,
        createdAt: new Date(),
        updatedAt: new Date(),
        __v: 0,
      },
    ]);
    setMessage("");
    // socket.emit("message", res.data);
  };

  //WEB SOCKET
  const socket = io("http://localhost:8080", {
    transports: ["websocket", "polling"],
  });

  // const [users, setUsers] = useState<any>([]);
  useEffect(() => {
    socket.emit("connexion", user?._id);
  }, [user]);

  // console.log(users);

  useEffect(() => {
    // socket.on("getUsers", (user: any) => {
    //   const newUser = {
    //     _id: user,
    //     username: "first",
    //     email: "firstEmail",
    //     password: "$2b$10$GSh2pLpCmxsg/cAWhScVA.dbKMSBLxMK.viZqiqBZNunJebqkx50m",
    //     createdAt: "2022-02-22T10:45:10.155+00:00",
    //     updatedAt: "2022-02-22T10:45:10.155+00:00",
    //     __v: 0,
    //   }
    //   setUsers(users);
    // });
    socket.on("getMessage", async ({ senderId, text, messageId }) => {
      //FETCH DATA
      const res = await axios.get(
        `http://localhost:5050/api/users/${senderId}`
      );
      if (!selectedConversation) return;
      setMessages([
        ...messages,
        {
          _id: messageId,
          content: text,
          userInfos: { _id: senderId },
          conversationID: selectedConversation._id,
          createdAt: new Date(),
          updatedAt: new Date(),
          __v: 0,
        },
      ]);
    });
  }, []);

  return (
    <div style={{ display: "flex" }}>
      <div style={{ marginRight: "100px" }}>
        {conversations.map((conversation) => {
          return (
            <div key={conversation._id}>
              {conversation.membersInfos.map((member) => {
                if (member._id !== user?._id)
                  return (
                    <div
                      onClick={() => setSelectedConversation(conversation)}
                      key={member._id}
                    >
                      {member.username}
                    </div>
                  );
              })}
            </div>
          );
        })}
      </div>
      <div>
        {messages.map((message) => {
          console.log(message.userInfos._id);
          return (
            <div
              key={message._id}
              style={
                message.userInfos._id === user?._id
                  ? { backgroundColor: "green" }
                  : { backgroundColor: "blue" }
              }
            >
              {message.content}
            </div>
          );
        })}
        <input
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="your message"
        ></input>
        <button onClick={sendMessage}>SEND</button>
      </div>
    </div>
  );
};

export default App;
